import React, { Component } from 'react'
import Swiper from 'swiper/dist/js/swiper.js'
import 'swiper/dist/css/swiper.min.css'
import css from './index.module.scss'
import axios from 'axios';
class Homeswiper extends Component {
    state = {
        datelist:[]
    }
    componentDidMount() {
        axios.get('http://api9.wochu.cn/client/v1/app/layoutamend?parameters=%7B%22version%22:%2210.0.0%22,%22source%22:%22H%22%7D').then(res => {
            console.log(res.data.data);
            // var dl = res.data.data.carousel
            // var str=[]
            // for (var i;i<dl.length;i++) {
            //     str.push()
            //  }
            this.setState({
                datelist: res.data.data.carousel
            })

        })
        // var mySwiper = 

    new Swiper ('.swiper-container', {
        loop: true,  //循环
        autoplay: {   //滑动后继续播放（不写官方默认暂停）
            disableOnInteraction: false,
        },
        pagination: {  //分页器
            el: '.swiper-pagination'
        }
    })
    }
    render() {
        return (
            <div className="App">
                < div className = "swiper-container" id={css.Homeswiper} >
                    <div className="swiper-wrapper" id={css.swiperwrapper}>
                        <div className="swiper-slide" id={css.swiperslide}>
                            {this.state.datelist.map((item, index) =>
                                <div className="swiper-slide" key={index}>
                                    <img src={item.picUrl} alt={item[index]}/>
                                </div>)}
                        </div>
                    </div>
                    {/* 如果需要分页 */}
                    <div className='swiper-pagination'></div>
                </div>
            </div>
        )       
    }
}


export default Homeswiper
